export const generateEntity = (name:string) => {
return `//generated by cli
import {
    Entity,
    PrimaryGeneratedColumn,
    Column,
    CreateDateColumn,
    UpdateDateColumn,
} from "typeorm";

@Entity("${name}s")
export class ${name} {
    @PrimaryGeneratedColumn()
    id: number;

    @Column({
    length: 100,
    })
    name: string;

    @CreateDateColumn()
    created_at: Date;

    @UpdateDateColumn()
    updated_at: Date;
    
}
`
}