export const generateValidator = (name: string) => {
  return `//generated by cli
  import * as joi from 'joi';
  import {${name}} from "../../../Entity/${name}";
  
  export const ${name}Fillable = joi.object({
    name: joi.string().required().max(100),
  });
  
  export interface I${name}Fillable extends ${name} {
    name: string;
  }
  `
};